import React, { useRef } from 'react';
import emailjs from '@emailjs/browser';

const styles = {
    form: {
      display: 'flex',
      flexDirection: 'column',
      width: '100%',
      maxWidth: '500px',
      margin: '0 auto',
      padding: '20px',
      borderRadius: '8px',
      backgroundColor: '#f9f9f9',
      boxShadow: '0 4px 8px rgba(201, 49, 49, 0.1)',
    },
    formGroup: {
      marginBottom: '15px',
    },
    label: {
      fontSize: '16px',
      fontWeight: '600',
      marginBottom: '5px',
    },
    input: {
      padding: '10px',
      fontSize: '16px',
      borderRadius: '5px',
      border: '1px solid #ddd',
      width: '100%',
      boxSizing: 'border-box',
    },
    textarea: {
      padding: '10px',
      fontSize: '16px',
      borderRadius: '5px',
      border: '1px solid #ddd',
      width: '100%',
      boxSizing: 'border-box',
      minHeight: '150px',
    },
    submitButton: {
      padding: '12px',
      fontSize: '16px',
      color: '#fff',
      backgroundColor: '#007BFF',
      border: 'none',
      borderRadius: '5px',
      cursor: 'pointer',
      transition: 'background-color 0.3s',
    },
  };
  
  

 const Contact = () => {
    const form = useRef();
  
    const sendEmail = (e) => {
      e.preventDefault();
  
      emailjs
        .sendForm('service_cbefiff', 'template_y64vetv', form.current, {
          publicKey: 'hq3P627Gnr6vrpMIw',
        })
        .then(
          () => {
            console.log('SUCCESS!');
          },
          (error) => {
            console.log('FAILED...', error.text);
          },
        );
    };
  
    return (
        <form ref={form} onSubmit={sendEmail} style={styles.form}>
        <div style={styles.formGroup}>
          <label style={styles.label}>Name</label>
          <input type="text" name="user_name" style={styles.input} required />
        </div>
        
        <div style={styles.formGroup}>
          <label style={styles.label}>Email</label>
          <input type="email" name="user_email" style={styles.input} required />
        </div>
      
        <div style={styles.formGroup}>
          <label style={styles.label}>Message</label>
          <textarea name="message" style={styles.textarea} required />
        </div>
        
        <input type="submit" value="Send" style={styles.submitButton} />
      </form>
      
    );
  };


export default Contact;
