import React from 'react';
import { Link } from 'react-router-dom';
import './Showcase.css'; // Imported the separate CSS file

const showcaseItems = [
    { 
        id: 1, 
        title: 'Beat Beam Smart LED', 
        image: '/Assets/Beat_beam-Smart LED.png',
        description: `BitBeam delivers smart, energy-efficient LED lighting solutions 
                      with customizable features for modern homes and workspaces.`,
        category: 'Brand Design'
    },
    { id: 2, title: 'LOGO: Crater Studio', image: '/Assets/project 2.png' },
    { id: 3, title: 'LOGO: House of Sage', image: '/Assets/project 3.png' },
];

const Showcase = () => (
    <div className="showcase">
        <h2>Showcase</h2>
        {showcaseItems.map(item => (
            <div key={item.id} className="showcase-item">
                <img src={item.image} alt={item.title} />
                <div className="showcase-content">
                    <Link to={`/showcase/${item.id}`} className="showcase-title">
                        {item.title}
                    </Link>
                    {item.id === 1 && (
                        <div className="bitbeam-info">
                            <p className="category">{item.category}</p>
                            <p className="description">{item.description}</p>
                        </div>
                    )}
                </div>
            </div>
        ))}
    </div>
);

export default Showcase;
