import React from 'react';
import { Link } from 'react-router-dom';

const showcaseItems = [
    { id: 1, title: 'Beat Beam Smart LED', image: '/.png' },  // ✅ Correct path for /public folder
    { id: 2, title: 'LOGO: Crater Studio', image: '/project 2.png' },
    { id: 3, title: 'LOGO: House of Sage', image: '/project 3.png' },
];

const Showcase = () => (
    <div className="showcase">
        <h2>Showcase</h2>
        {showcaseItems.map(item => (
            <div key={item.id} className="showcase-item">
                <img src={item.image} alt={item.title} />
                <Link to={`/showcase/${item.id}`}>{item.title}</Link>
            </div>
        ))}
    </div>
);

export default Showcase;
