import React from 'react';
import './Home.css';
import profileImage from '../assets/pranil.jpeg'; // Ensure your image is inside /src/assets/
import Contact from './Contact';
import Archive from './Archive';
import { useState } from 'react';

const Home = () => {
    const [showContact, setShowContact] = useState(false);

    // Button click handler to trigger the display of the Contact component
    const handleButtonClick = () => {
        setShowContact(true);  // Update state to show the Contact component
    };

    (
    <div className="home">
        <div className="content">
            <h1 >
                Hello, <span className="highlight">I'm Pranil.</span>
            </h1>
            <h2>Graphic Designer in Pune.</h2>
            <p>
                I design with clarity and purpose, creating visuals that are both useful and reliable. 
                Every project is an opportunity to solve problems through thoughtful and effective design.
            </p>
            <div className="buttons">
                <button className="btn primary" onClick={handleButtonClick}>Get in Touch</button>
                {showContact && <Contact/>}
                <button className="btn secondary" onClick={handleButtonClick}>View Work</button>
                {showContact && <Archive/>}
                
            </div>
        </div>

        <div className="image-container">
            <img src={profileImage} alt="Pranil" />
        </div>
    </div>
)};

export default Home;
