import React, { useState } from 'react';
import './Home.css';
import profileImage from '../assets/pranil.jpeg'; // Ensure your image is inside /src/assets/
import Contact from './Contact';
import Archive from './Archive';

const Home = () => {
  // State to manage visibility of Contact and Archive components
  const [showContact, setShowContact] = useState(false);
  const [showArchive, setShowArchive] = useState(false);

  // Button click handler for "Get in Touch"
  const handleContactClick = () => {
    setShowContact(true);  // Show the Contact component
    setShowArchive(false); // Hide the Archive component if shown
  };

  // Button click handler for "View Work"
  const handleArchiveClick = () => {
    setShowArchive(true);  // Show the Archive component
    setShowContact(false); // Hide the Contact component if shown
  };

  return (
    <div className="home">
      <div className="content">
        <h1>
          Hello, <span className="highlight">I'm Pranil.</span>
        </h1>
        <h2>Graphic Designer in Pune.</h2>
        <p>
          I design with clarity and purpose, creating visuals that are both useful and reliable. 
          Every project is an opportunity to solve problems through thoughtful and effective design.
        </p>
        <div className="buttons">
          <button className="btn primary" onClick={handleContactClick}>
            Get in Touch
          </button>
          {showContact && <Contact />} {/* Conditionally render Contact component */}

          <button className="btn secondary" onClick={handleArchiveClick}>
            View Work
          </button>
          {showArchive && <Archive />} {/* Conditionally render Archive component */}
        </div>
      </div>

      <div className="image-container">
        <img src={profileImage} alt="Pranil" />
      </div>
    </div>
  );
};

export default Home;
